 /////////////////////////////////////////////////////////////////////////////////////
 // Functions for communicating with MIFARE PICCs
 /////////////////////////////////////////////////////////////////////////////////////
 byte PCD_Authenticate(byte command, byte blockAddr, MIFARE_Key *key, Uid *uid);
